        }
    }

    .stepHeader {
        padding: 0 0 30px 0;
        position: relative;
        flex-wrap: wrap;

        .stepHeader-body {
            margin: 0;

            .customerView {
                margin: 28px 0 0 0;
            }
        }

        .stepHeader-actions {
            position: absolute;
            top: 12px;
            right: 0;
            margin: 0;
            z-index: 10;

            .optimizedCheckout-buttonSecondary {
                border-radius: 0;
                background-color: transparent;
                border: none;
                padding: 0;
                height: auto;
                line-height: 26px;
                font-size: stencilNumber("fontSize-root");
                font-weight: normal;
                font-style: normal;
                box-shadow: none;
                text-transform: capitalize;
                color: stencilColor("optimizedCheckout-link-hoverColor");
