components/common/payment-icons:
{{#with theme_settings}}
    {{#or show_accept_amex show_accept_discover show_accept_mastercard show_accept_paypal show_accept_visa show_accept_amazonpay show_accept_googlepay show_accept_klarna}}
    <div class="footer-payment-icons">
        {{#if show_accept_visa}}
            <svg class="footer-payment-icon" role="img" aria-label="Visa">
                <title>Visa</title>
                <desc>Visa</desc>
                <use xlink:href="#icon-logo-visa"></use>
            </svg>
        {{/if}}
        {{#if show_accept_mastercard}}
            <svg class="footer-payment-icon" role="img" aria-label="Mastercard">
                <title>Mastercard</title>
                <desc>Mastercard</desc>
                <use xlink:href="#icon-logo-mastercard"></use>
            </svg>
        {{/if}}
        {{#if show_accept_amex}}
            <svg class="footer-payment-icon" role="img" aria-label="Amex">
                <title>Amex</title>
                <desc>Amex</desc>
                <use xlink:href="#icon-logo-american-express"></use>
            </svg>
        {{/if}}
        {{#if show_accept_discover}}
            <svg class="footer-payment-icon" role="img" aria-label="Discover">
                <title>Discover</title>
                <desc>Discover</desc>
                <use xlink:href="#icon-logo-discover"></use>
            </svg>
        {{/if}}
        {{#if show_accept_paypal}}
            <svg class="footer-payment-icon" role="img" aria-label="Paypal">
                <title>Paypal</title>
                <desc>Paypal</desc>
                <use xlink:href="#icon-logo-paypal"></use>
            </svg>
        {{/if}}
        {{#if show_accept_amazonpay}}
            <svg class="footer-payment-icon" role="img" aria-label="Amazonpay">
                <title>Amazonpay</title>
                <desc>Amazonpay</desc>
                <use xlink:href="#icon-logo-amazonpay"></use>
            </svg>
        {{/if}}
        {{#if show_accept_googlepay}}
            <svg class="footer-payment-icon" role="img" aria-label="Google Pay">
                <title>Google Pay</title>
                <desc>Google Pay</desc>
                <use xlink:href="#icon-logo-googlepay"></use>
            </svg>
        {{/if}}
        {{#if show_accept_klarna}}
            <svg class="footer-payment-icon" role="img" aria-label="Klarna">
                <title>Klarna</title>
                <desc>Klarna</desc>
                <use xlink:href="#icon-logo-klarna"></use>
            </svg>
        {{/if}}
        {{#if show_accept_estern_union}}
            <svg class="footer-payment-icon" role="img" aria-label="Estern Union">
                <title>Estern Union</title>
                <desc>Estern Union</desc>
                <use xlink:href="#icon-logo-estern-union"></use>
            </svg>
        {{/if}}
    </div>
    {{/or}}
{{/with}}

components/halothemes/popup/halo-recentl:
{{#if theme_settings.haloRecentlyBought}}
	<div id="halo-recently-bought-popup" class="halo-popup halo-recently-bought-popup">
		<div class="hide halo-recently-bought-list" id="halo-recently-bought-list"></div>
	</div>
{{/if}}

components/common/navigation-list-altern:
{{#if children}}
    <p class="navPages-action has-subMenu is-root" data-label="{{url}}">
        <a class="text" href="{{url}}">{{name}}</a>
        <span class="navPages-action-moreIcon" aria-hidden="true">
            <svg class="icon"><use xlink:href="#icon-chevron-down" /></svg>
        </span>
    </p>
    {{> components/common/navigation-dropdown}}
{{else}}
    <a class="navPages-action navPages-action-end" data-label="{{url}}" href="{{url}}">
      <span class="text">{{name}}</span>
    </a>
{{/if}}
